import './Checklist.css';

import React, {useState} from 'react';

const List = (props) => (
    <ul className="list">
      {props.items.map(item => <Item {...item} />)}
    </ul>
  );

const Item = () => {
    return (
        <li class="item">
            <div class="content">
                <input type="text"/>
            </div>
            <span class="delete">✗</span>
        </li>
        // <li className="item">
        //     <div className="content">hahaha</div>
        //     <span className="delete">✗</span>
        // </li>
    );
}

const Counter = ({maxCount}) => {
    const maxValue = maxCount;

    return (
        <span className="counter">{`0 von ${maxValue}`}</span>
    );
}

const Checklist = ({title, maxCount}) => {
    const [items, setItem] = useState([]);

    const addNewItem = (newItem) => {
        setItem([...items, newItem]);
    };

    return (
        <div className="checklist">
            <h2 className="title">
                {title} <Counter maxCount={maxCount}/>
            </h2>
            <List items={items}/>
            <button onClick={addNewItem}>+</button>
        </div>
    );
}

export default Checklist
