import './Checklist.css';

import React, {useEffect, useState} from 'react';

import data from "./data.json";


//
// Counter.jsx
//
const Counter = ({count, maxCount}) => (
  <span className="counter">{`${count} von ${maxCount}`}</span>
)

//
// ChecklistNew.jsx
//
const ChecklistNew = ({title, maxCount}) => {
  const [items, setItem] = useState(data);

  const addItem = (newItem) => {
    setItem([...items, newItem]);
  }

  const [count, setCount] = useState(items.length);

  useEffect(() => {
    let counter = setCount(items.length);

    return function cleanup() {
      clearInterval(counter);
    };
  }, []);

//


  // const toggleState = () => {
  //   setToDos(!toDos);
  // }

  // const handleMyClick = (e) => {
  //   e.preventDefault()
  //   handleDeletion(e.currentTarget.parentNode.id)
  // }

  const [toDos, setToDos] = useState(data);

  const handleToggle = (e) => {
      e.preventDefault()

      let mapped = toDos.map(item => {
        return (item.id === Number(e.currentTarget.parentNode.id) ? { ...item, complete: !item.complete } : { ...item});
      });
      setToDos(mapped);
  }

  // const handleDeletion = (id) => {
  //   let updateList = items.filter((item) => item.id !== id);
  //   setItem([updateList]);
  //   console.log([updateList]);
  // }

  const handleDeletion = (e) => {
      e.preventDefault()

      let filteredList = toDos.filter((item) => item.e !== e);
      setToDos(filteredList);
      // console.log([filteredList]);
  }

  return (
    <div className="checklist">
      <h2 className="title">
        {title} <Counter count={count} maxCount={maxCount}/>
      </h2>

        <ul className="list">

          {toDos.map((item) => {

            return (
              <li key={item.id} id={item.id} className="item">
                <div onClick={handleToggle} className={item.complete ? 'toDos' : ''}>{item.content}</div>
                <span onClick={handleDeletion} className="delete">âœ—</span>
              </li>
              )
            })}

        </ul>


      {count < maxCount &&
        <button onClick={addItem}>+</button>
      } 
    </div>
  );
}

export default ChecklistNew;
