const ThemeSwitch = ({setTheme}) => {

    const handleClick = (mode) => {
        setTheme(mode);
        localStorage.setItem("theme", mode);
    }

    return ( 
        <div className="themeSwitch">
            {/* <span onClick={() => handleClick('light')}>Light</span>
            <div className="toggle"><span className="O"></span></div>
            <span onClick={() => handleClick('dark')}>Dark</span> */}
            <div className="toggleLeft" onClick={() => handleClick('light')}>Light<span className="leftO"></span></div>
            <div className="toggleRight" onClick={() => handleClick('light')}>Dark<span className="rightO"></span></div>
        </div>
     );
}

export default ThemeSwitch;
