import './Checklist.css';

import React, {createElement} from 'react';

// import ReactDOM from 'react-dom';

// import { useState } from 'react';

// const List = () => {
//     return (
//         <ul className="list">
//             <Item/>
//         </ul>
//     );
// }

const Item = () => {
    return (
        <li class="item">
            <div class="content">
                <input type="text"/>
            </div>
            <span class="delete">✗</span>
        </li>
        // <li className="item">
        //     <div className="content">hahaha</div>
        //     <span className="delete">✗</span>
        // </li>
    );
}

const Counter = ({count}) => {
    const maxValue = count;
    // const [value, setValue] = useState;

    return (
        <span className="itemNumb">{`2 von ${maxValue}`}</span>
    );
}

const Checklist = ({title, count}) => {
    // function createBlubb() { console.log('Create!'); }
    // const x = document.getElementsByClassName('list')[0];
    // function createItem() { 
    //     const newItem = createElement(<Item/>); 
    //     console.log(newItem);
    // };

    const [newItem, setNewItem] = useState(<Item/>);

    return (
        <div className="container">
            <h2>
                {title} <Counter count={count}/>
            </h2>
            <ul className="list">
                {/* <Item/> */}
                {newItem}
            </ul>
            <button onClick={(e) => setNewItem(createElement(<Item/>))}>+</button>
        </div>
    );
}

export default Checklist
