import './Checklist.css';

import React, {createElement, useState} from 'react';

// import ReactDOM from 'react-dom';

const List = (props) => (
    <div>
      {/* {props.newItem.map(item => <Item key={item.id} {...item} />)} */}
      {props.newItem.map(item => <Item {...item} />)}
    </div>
  );

const Item = () => {
    return (
        <li class="item">
            <div class="content">
                <input type="text"/>
            </div>
            <span class="delete">✗</span>
        </li>
        // <li className="item">
        //     <div className="content">hahaha</div>
        //     <span className="delete">✗</span>
        // </li>
    );
}

const Counter = ({count}) => {
    const maxValue = count;
    // const [value, setValue] = useState;

    return (
        <span className="itemNumb">{`2 von ${maxValue}`}</span>
    );
}

const Checklist = ({title, count}) => {
    // function createBlubb() { console.log('Create!'); }
    // const x = document.getElementsByClassName('list')[0];
    // function createItem() { 
    //     const newItem = createElement(<Item/>); 
    //     console.log(newItem);
    // };

    const [newItem, setNewItem] = useState([]);

    const addNewItem = (newItemData) => {
        setNewItem([...newItem, newItemData]);
    };

    return (
        <div className="container">
            <h2>
                {title} <Counter count={count}/>
            </h2>
            <ul className="list">
                {/* {newItem} */}
                <List newItem={newItem}/>
            </ul>
            <button onClick={addNewItem}>+</button>
        </div>
    );
}

export default Checklist
