import './App.css';

import React, { useState } from 'react';

import Checklist from './Checklist/Checklist';
import Clock from './Clock';
import GitHub from './GitHub';

// import ThemeSwitch from './ThemeSwitch';

const App = () => {
  const [theme, setTheme] = useState('light');

  const ThemeSwitch = () => {
    const handleClick = (mode) => {
        setTheme(mode);
    }
    return ( 
        <div className="themeSwitch">
            <button onClick={() => handleClick('dark')}>Dark</button>
            <button onClick={() => handleClick('light')}>Light</button>
        </div>
     );
}

  return (
    <div className={'App ' + theme}>
      <GitHub href='https://github.com/azwick/dashboard' />
      {/* <ThemeSwitch theme={theme} setTheme={setTheme} />
       */}
      <ThemeSwitch />
      <div className="container">
        <Clock/>
        <div className="grid">
          <Checklist title='Prio A' maxCount={3} />
          <Checklist title='Prio B' maxCount={3} />
          <Checklist title='Prio C' maxCount={3} />
        </div>
      </div>
    </div>
  );
}

export default App;
